// Get data from HTTP Request response
let publications = [];

try {
  // The HTTP Request returns data in $input.all()
  const response = $input.all();

  if (response && response.length > 0) {
    publications = response.map(item => item.json);
  }
} catch (error) {
  console.log('Error loading publications:', error);
}

// Calculate statistics
const totalPubs = publications.length;
const yearCounts = {};
const organismCounts = {};
const areaCounts = {};

publications.forEach(pub => {
  // Year distribution
  if (pub.year) {
    yearCounts[pub.year] = (yearCounts[pub.year] || 0) + 1;
  }

  // Organism counts
  if (pub.organisms && Array.isArray(pub.organisms)) {
    pub.organisms.forEach(org => {
      organismCounts[org] = (organismCounts[org] || 0) + 1;
    });
  }

  // Research area counts
  if (pub.research_areas && Array.isArray(pub.research_areas)) {
    pub.research_areas.forEach(area => {
      areaCounts[area] = (areaCounts[area] || 0) + 1;
    });
  }
});

const html = `
