const csv = $input.first().json.data;
const lines = csv.split('\n').filter(line => line.trim());
const publications = [];

const seenPmcs = new Set(); // store already found pmcid values

for (let i = 0; i < lines.length; i++) {
  const line = lines[i];

  // Split by comma that comes before http
  const parts = line.split(',http');
  if (parts.length === 2) {
    const title = parts[0].replace(/^"|"$/g, '').trim();
    const link = 'http' + parts[1].trim();
    const pmcMatch = link.match(/PMC(\d+)/);
    const pmcid = pmcMatch ? pmcMatch[0] : null;

    // Skip duplicates or invalid entries
    if (!pmcid || seenPmcs.has(pmcid)) continue;

    seenPmcs.add(pmcid);

    publications.push({
      json: {
        title,
        link,
        pmcid
      }
    });
  }
}

return publications;

const xmlData = $json.data;
const pmcid = $json.pmcid;
const title = $json.title;
const link = $json.link;

// More flexible extraction patterns
const abstract = xmlData.match(/<abstract[^>]*>(.*?)<\/abstract>/si)?.[1] || '';
const body = xmlData.match(/<body[^>]*>(.*?)<\/body>/si)?.[1] || '';

// Clean HTML/XML tags
const cleanText = (text) => text.replace(/<[^>]*>/g, ' ').replace(/\s+/g, ' ').trim();

return {
  pmcid: pmcid,
  title: title,
  link: link,
  abstract: cleanText(abstract).substring(0, 1000),
  content_sample: cleanText(body).substring(0, 2000),
  processed: true
};

