// Get full AI response object
const aiResponse = $json.choices?.[0]?.message?.content || '';
const aiRole = $json.choices?.[0]?.message?.role || 'assistant';
const finishReason = $json.choices?.[0]?.finish_reason || '';

// Get original data from Node 3 (Code in JavaScript) which has title, link, pmcid
const originalData = $('Code in JavaScript').item.json;

// Get processed data from Node 7 (Code in JavaScript1) which has abstract and content
const processedData = $('Code in JavaScript1').item.json;

// Parse AI response to extract structured insights
let keyFindings = [];
const findingsMatch = aiResponse.match(/1\.\s*\*?\*?(.*?)[\n\r]+2\.\s*\*?\*?(.*?)[\n\r]+3\.\s*\*?\*?(.*?)(?:$|\n)/s);
if (findingsMatch) {
  keyFindings = [
    findingsMatch[1].replace(/\*+/g, '').trim(),
    findingsMatch[2].replace(/\*+/g, '').trim(),
    findingsMatch[3].replace(/\*+/g, '').trim()
  ];
}

// Combine all text for analysis
const fullText = (processedData.abstract + ' ' + processedData.content_sample + ' ' + aiResponse).toLowerCase();

// EXTRACT SCIENTIFIC ROLES/EXPERTISE
const researchRoles = [];
if (fullText.match(/biolog|molecular|genetic|genom/)) researchRoles.push('biologist');
if (fullText.match(/physic|mechanic|engineer/)) researchRoles.push('physicist');
if (fullText.match(/medic|clinic|physician|doctor|health/)) researchRoles.push('medical_researcher');
if (fullText.match(/stem cell|regenerat|tissue/)) researchRoles.push('regenerative_medicine');
if (fullText.match(/bone|skeletal|osteo/)) researchRoles.push('bone_specialist');
if (fullText.match(/immunolog|antibod|lymph/)) researchRoles.push('immunologist');
if (fullText.match(/psycholog|cognitive|behavior/)) researchRoles.push('psychologist');
if (fullText.match(/chemist|biochem/)) researchRoles.push('chemist');
if (fullText.match(/nutrition|diet/)) researchRoles.push('nutritionist');

// EXTRACT RESEARCH DOMAINS
const researchDomains = [];
if (fullText.match(/space|microgravity|weightless|orbit/)) researchDomains.push('space_biology');
if (fullText.match(/gene|RNA|DNA|PCR|genom/)) researchDomains.push('genomics');
if (fullText.match(/cell|cellular/)) researchDomains.push('cell_biology');
if (fullText.match(/proteom|protein/)) researchDomains.push('proteomics');
if (fullText.match(/tissue engineer/)) researchDomains.push('tissue_engineering');
if (fullText.match(/cancer|tumor|oncolog/)) researchDomains.push('oncology');

// Extract mission relevance and space complexity
const relevanceMatch = aiResponse.match(/mission|Mars|Moon|astronaut|space|ISS/gi);
const relevanceScore = relevanceMatch ? relevanceMatch.length : 0;
const missionRelevance = relevanceScore > 5 ? 'high' : relevanceScore > 2 ? 'medium' : 'low';

// Analyze space complexity factors
const spaceFactors = {
  microgravity: (fullText.match(/microgravity|weightless|zero.?gravity/gi) || []).length,
  radiation: (fullText.match(/radiation|cosmic|solar/gi) || []).length,
  isolation: (fullText.match(/isolation|confine|psychological/gi) || []).length,
  duration: (fullText.match(/\d+\s*(day|month|year|week)/gi) || []).length
};

// Calculate space complexity score (0-10)
const spaceComplexity = Math.min(10, 
  spaceFactors.microgravity * 2 + 
  spaceFactors.radiation * 2 + 
  spaceFactors.isolation + 
  spaceFactors.duration
);

// Extract biological systems affected
const biologicalSystems = [];
if (fullText.match(/bone|skeletal|osteo/)) biologicalSystems.push('skeletal');
if (fullText.match(/muscle|muscular|myocyte/)) biologicalSystems.push('muscular');
if (fullText.match(/immune|T cell|lymph|macrophage/)) biologicalSystems.push('immune');
if (fullText.match(/cardio|heart|vascular|blood/)) biologicalSystems.push('cardiovascular');
if (fullText.match(/stem cell|pluripotent|differentiation/)) biologicalSystems.push('stem_cell');
if (fullText.match(/neural|brain|cognitive|neuron/)) biologicalSystems.push('neurological');
if (fullText.match(/kidney|renal|nephro/)) biologicalSystems.push('renal');
if (fullText.match(/liver|hepat/)) biologicalSystems.push('hepatic');

// Check for usage/rating if provided by API
const usage = $json.usage || null;
const modelUsed = $json.model || 'llama-3.3-70b-versatile';

// Add confidence score based on response quality
const confidenceScore = keyFindings.length === 3 ? 'high' : keyFindings.length > 0 ? 'medium' : 'low';

// Extract experiment duration if mentioned
let experimentDuration = null;
const durationMatch = fullText.match(/(\d+)[\s-]*(day|week|month|year)/);
if (durationMatch) {
  const value = parseInt(durationMatch[1]);
  const unit = durationMatch[2];
  if (unit === 'day') experimentDuration = value;
  else if (unit === 'week') experimentDuration = value * 7;
  else if (unit === 'month') experimentDuration = value * 30;
  else if (unit === 'year') experimentDuration = value * 365;
}

// Extract publication year if mentioned
let publicationYear = null;
const yearMatch = fullText.match(/\b(19\d{2}|20\d{2})\b/);
if (yearMatch) {
  publicationYear = parseInt(yearMatch[1]);
}

return {
  pmcid: originalData.pmcid,
  title: originalData.title,
  link: originalData.link,
  abstract: processedData.abstract,
  content_sample: processedData.content_sample,
  ai_response: aiResponse,
  ai_role: aiRole,
  ai_model: modelUsed,
  ai_confidence: confidenceScore,
  key_findings: keyFindings,
  findings_count: keyFindings.length,
  research_roles: researchRoles,
  research_domains: researchDomains,
  mission_relevance: missionRelevance,
  relevance_score: relevanceScore,
  space_complexity: spaceComplexity,
  space_factors: spaceFactors,
  biological_systems: biologicalSystems,
  experiment_duration_days: experimentDuration,
  publication_year: publicationYear,
  finish_reason: finishReason,
  usage_stats: usage,
  processed: true,
  processed_at: new Date().toISOString()
};
